mapper.update:
update sysInfo
set ifRead=true
where to_id=#{id};

mapper.select:
SELECT *
FROM sysInfo
WHERE to_id=#{id}
ORDER BY created_time ASC;

SELECT *
FROM user
WHERE user.id in (
    SELECT distinct(tmp_id)
    FROM (
        SELECT from_id as tmp_id,created_time
        FROM sysInfo
        WHERE to_id=#{id}
        UNION
        SELECT to_id as tmp_id,created_time
        FROM sysInfo
        WHERE from_id=#{id}
        ORDER BY created_time DESC
    ) as tmp
);

SELECT *
FROM (
    SELECT *
    FROM sysInfo
    WHERE to_id=#{id}
    ORDER BY created_time DESC
) as tmp LIMIT 1;

SELECT count(*)
FROM sysInfo
WHERE to_id=#{id} and ifRead=false;
